mapper.update:
update t_station_info set f_station_name = #{stationName}, f_station_lon = #{stationLon}, f_station_lat = #{stationLat}, f_station_address = #{stationAddress}, f_process_tech = #{processTech}, f_process_scale = #{processScale}, f_dev_ops_num = #{devOpsNum}, f_principal = #{principal}, f_contact_tel = #{connectTel}, f_bg_dev_img_path = #{bgDevImgPath}, f_bg_water_img_path = #{bgWaterImgPath}, f_remarks1 = #{remarks1},
f_remarks2 = #{remarks2}, f_remarks3 = #{remarks3}
f_remarks2 = #{remarks2}
where f_station_no = #{stationNo}

mapper.select:
select count(1) as total
from t_station_info
where f_station_name like #{stationName}

mapper.update:
update t_device_info
set f_dev_name       = #{devName},
    f_dev_type       = #{devType},
    f_dev_var_fields = #{devVarFields},
    f_dev_vector     = #{deviceVector}
where f_dev_no = #{devNo}
  and f_station_no = #{stationNo}

update t_device
set f_end_receive_time   = #{endReceiveTime},
    f_start_receive_time = f_start_receive_time,
    f_receive_count      = f_receive_count + 1
where f_station_no = #{stationNo}
  and f_var_list_fields_md5 = #{varListFieldsMd5}
  and f_is_show = 1;

mapper.select:
select count(1) as total
from t_device
where MD5(f_var_list_fields) = #{varList4FieldsMd5}
  and f_station_no = #{stationNo}
  and f_dev_no = #{devNo}
  and f_is_show = 1;

mapper.update:
update t_device
set f_is_show = 0
where f_dev_no = #{devNo}
